import dateFormat from 'dateformat'
import './CardDetail.css'
import { Link, useNavigate  } from 'react-router-dom';
import { Context } from '../../Store/AppContext';

const CardDetail = (props) => {
  
  const { id, id_city, date_weather,country } = props
  const date = dateFormat(date_weather,"dddd, mmmm dS, yyyy")
  const navigate = useNavigate();

    return (
      <div className="card my-5" style={{width:"18rem"}}>
        <div className="card-body">
          <h5 className="card-title">{date}</h5>
          <p className="card-text"></p>
        </div>
        <ul className="list-group list-group-flush">
          <li className="list-group-item"></li>
          <li className="list-group-item"></li>
          <li className="list-group-item">vacio</li>
        </ul>
        <div className="card-body">
        <Link to="/"><button   type="button" className="btn btn-danger mt-5" style={{background: "linear-gradient(333deg, rgba(5,3,170,1) 19%, rgba(184,1,103,1) 70%)" }}><i className="bi bi-arrow-left me-2"></i>Volver</button></Link>
        </div>
      </div>
    );
}

export default CardDetail



