import React, { useContext, useEffect, useState } from 'react'
import {useParams} from 'react-router-dom'
import { Context } from '../../Store/AppContext'
import axios from 'axios'
import { BASE_URL, BASE_URL_TEMPERATURE } from '../../utils'
import CardDetail from '../../Components/CardDetail/CardDetail'


const DetailDay = () => {
  const {city} = useContext(Context)
  // const friday = city[0].friday
  // console.log(friday)
  const [cityId, setCityId] = useState([])
  const {id,friday} = useParams()
  
  const apiCityId = async () => {
    const response = await axios.get(`${BASE_URL_TEMPERATURE}/${id}/${friday}`)
    setCityId(response.data.data[0])
    console.log(response.data.data[0])
  }
  
  useEffect(() => {
    apiCityId()
  }, [])

  return (
    <div className='mxd-flex flex-wrap justify-content-between'>
      <CardDetail key={cityId.id} {...cityId} />
    </div>
   
  )
}

export default DetailDay
