import React from 'react'
import './CardDetail.css'
import { Link, useNavigate  } from 'react-router-dom';

const CardDetail = (props) => {
  const { id, brand, model } = props
  const navigate = useNavigate();
console.log(id)
    return (
      <div className="card "style={{background: "linear-gradient(0deg, rgba(107,33,153,1) 9%, rgba(147,98,226,1) 81%)"}}>
            <div className="row g-0">
                <div className="col-md-7">
                    <div className="card-body">
                        <h5 className="card-title fs-1 mb-5 text-white">{id}</h5>
                        <p className="card-text fs-5 text-white">{brand}</p>
                        <p className="card-text fs-6"><small className="text-dark d-flex justify-content-end me-5 mt-4 mb-5 text-white">Calificaci√≥n: {model}<i className="bi bi-star-fill text-warning ms-3"></i></small></p>
                        <Link to="#"><button  onClick={()=>{navigate(-1)}} type="button" className="btn btn-danger mt-5" style={{background: "linear-gradient(333deg, rgba(5,3,170,1) 19%, rgba(184,1,103,1) 70%)" }}><i className="bi bi-arrow-left me-2"></i>Volver</button></Link>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default CardDetail
