import React, { useContext } from 'react'
import dateFormat from 'dateformat'
import './CardDetail.css'
import { Link, useNavigate  } from 'react-router-dom';
import { Context } from '../../Store/AppContext';

const CardDetail = (props) => {
  
  const { date_weather,Twelve_AM, Forecast_Twelve_AM
  } = props
  const date = dateFormat(date_weather,"dddd")
  const navigate = useNavigate();

    return (
      <div className='d-flex border w-100 mt-5'>
          <div className="card border-light fw-bold mb-3" style={{width: "10%", height:"20rem"}}>
            <div className="card-header">{date}</div>
            <div className="d-flex align-items-end card-body mb-4">
              <h5 className="card-title fw-normal">Forecast</h5>
            </div>
            <div className="card-header fw-normal">Temp (°C)</div>
          </div>
          <div className="card border-light mb-3" style={{width: "25%", height:"20rem"}}>
            <div className="card-header">{date}</div>
            <div className="d-flex align-items-end card-body mb-4">
              <h5 className="card-title fw-normal">Forecast</h5>
            </div>
            <div className="card-header fw-normal">Temp (°C)</div>
          </div>
      </div>
      
      
    );
}

export default CardDetail
